function randColor(){
    let r = Math.floor(Math.random()*256);
    let g = Math.floor(Math.random()*256);
    let b = Math.floor(Math.random()*256);
    let Rgb = "rgb("+r+", "+g+","+b+")";
    const [bgColor, setBgColor] = useState(Rgb);
    const changColor =()=>{
        setBgColor(rgb);
    }
    return(
        <nav id="randNav" style = {{backgroundColor: Rgb}}></nav>
    );
}

export default randColor;
