import React, { Fragment } from 'react';
import randColor from '../components/randColor';
/*
const colors = () => {
  return (
    <Fragment>
      <div className='container'>
        <h1>
          <i className='fas fa-exclamation-triangle'></i> Random Color!
        </h1>
        <button id="color" onClick={randColor}>Change color</button>
      </div>
    </Fragment>
    
  );
};
*/

function colors() {
  let r = Math.floor(Math.random()*256);
  let g = Math.floor(Math.random()*256);
  let b = Math.floor(Math.random()*256);
  let Rgb = "rgb("+r+", "+g+","+b+")";
  const [bgColor, setBgColor] = useState(Rgb);
   const changeColor =()=>{
    
      setBgColor(Rgb);
    }
  return (
    <div className="App">
      <header className="App-header" style={{background: bgColor}}>
        <button onClick={changeColor}>Change Color</button>
      </header>
    </div>
  );
}


export default colors;
