import React, { Fragment, useEffect, useState } from 'react';
import axios from 'axios';

let apiUrl = "";
const apiUrls = [
  "https://random-d.uk/"
];

function randomizer(){
  apiUrl = apiUrls[Math.floor(Math.random()*apiUrls.length)]
}

class Images extends React.Component{
  constructor(props){
    super(props);
    this.state = {img:''};
  };
  componentDidMount(){
    axios.get('https://api.unsplash.com/search/photos', {
      params:{
        query: 'flower'
      },
      headers: {
        Authorization: 'cdommer01 0GOLsl1vsWIqxwx7J-gWqfdZFnvdg1f8da6DLhI5Rfc'
      }

    });
  }
  render(){
    return(
      <Fragment>
        <div className='container'>
          <h1>
            <i className='fas fa-exclamation-triangle'></i> Random Image
          </h1>
          <div>
            <img src={this.img} />
            <button>Get a random image</button>
            <button onClick={randomizer()}>Refresh</button>
          </div>
        </div>
      </Fragment>
    );
  }
};

export default Images;
