import { Component, useState } from "react";

/*
const randColor = () =>{
    let r = Math.floor(Math.random()*256);
    let g = Math.floor(Math.random()*256);
    let b = Math.floor(Math.random()*256);
    console.log("rgb("+r+","+g+","+b+")");
    document.getElementById("randNav").style.backgroundColor = "rgb("+r+","+g+","+b+") !important";
}
*/
function rgbMaker(){
    let r = Math.floor(Math.random()*256);
    let g = Math.floor(Math.random()*256);
    let b = Math.floor(Math.random()*256);
    return "rgb("+r+", "+g+","+b+")";
}


function randColor() {
    let Rgb = new rgbMaker();
    const [bgColor, setBgColor] = useState(Rgb);
        const changeColor =()=>{
            let NewRGB = new rgbMaker();
            setBgColor(NewRGB);
        }
    return (
        <div className="App">
        <header className="App-header" style={{background: bgColor}}>
            <button onClick={changeColor}>Change Color</button>
        </header>
        </div>
    );
}

export default randColor;
