import React, { useState } from 'react';
import Navbar from '../components/Navbar';
/*
const colors = () => {
  return (
    <Fragment>
      <div className='container'>
        <h1>
          <i className='fas fa-exclamation-triangle'></i> Random Color!
        </h1>
        <button id="color" onClick={randColor}>Change color</button>
      </div>
    </Fragment>
    
  );
};
*/

function GetRGB(){
  let r = Math.floor(Math.random()*256);
  let g = Math.floor(Math.random()*256);
  let b = Math.floor(Math.random()*256);
  return "rgb("+r+", "+g+","+b+")";
}

function Colors() {
  
  const [bgColor, setBgColor] = useState(GetRGB());
   const changeColor =()=>{
      setBgColor(GetRGB());
    }
  return (
    <div className="App">
      <header className="App-header" style={{background: bgColor}}>
        <button onClick={Navbar.changeColorNav}>Change Color</button>
      </header>
    </div>
  );
}


export default Colors;
