import React, { useState } from 'react';
import Navbar from '../components/Navbar';
/*
const colors = () => {
  return (
    <Fragment>
      <div className='container'>
        <h1>
          <i className='fas fa-exclamation-triangle'></i> Random Color!
        </h1>
        <button id="color" onClick={randColor}>Change color</button>
      </div>
    </Fragment>
    
  );
};
*/

function GetRGB(){
  let r = Math.floor(Math.random()*256);
  let g = Math.floor(Math.random()*256);
  let b = Math.floor(Math.random()*256);
  return "rgb("+r+", "+g+","+b+")";
}

function Colors() {
  
  const [bgColor, setBgColor] = useState(GetRGB());
   const changeColor =()=>{
      setBgColor(GetRGB());
    }
  return (
    <div className="App">
      <nav id="randNav" style={{backgroundColor: GetRGB()}}>
              <div className="nav-wrapper">
              <Link to="/" className="brand-logo center">Random</Link>
              <ul id="nav-mobile" className="right">
                  <li><Link to='/'>Text</Link></li>
                  <li><Link to='/Image'>Image</Link></li>
                  <li><Link to='/Color'>Color</Link></li>
                  <li><Link to='/API'>API</Link></li>
                  <li><Link to='/About'>About</Link></li>
              </ul>
              </div>
      </nav>
        <button onClick={changeColor}>Change Color</button>
    </div>
  );
}


export default Colors;
