import React, { Fragment, useEffect, useState } from 'react';
import axios from 'axios';

let apiUrl = "";
const apiUrls = [
  "https://random-d.uk/"
];

function randomizer(){
  apiUrl = apiUrls[Math.floor(Math.random()*apiUrls.length)]
}

function showImage(){
  return images.map((img, index) =>{
    <Image 
      image={img.urls.regular}
      index={index}
      key={index}
    />
  })
}

function Images(){
  
  const [images, setimages] = useState([]);
  
  useEffect(()=>{
    axios.get('https://api.unsplash.com/photos/?client_id=0GOLsl1vsWIqxwx7J-gWqfdZFnvdg1f8da6DLhI5Rfc')
    .then((res)=>{
      setimages(res.data);
    });
  }, []);
  return(
    <Fragment>
      <div className='container'>
        <h1>
          <i className='fas fa-exclamation-triangle'></i> Random Image
        </h1>
        <div>
          <img src={images} />
          <button>Get a random image</button>
          <button onClick={randomizer()}>Refresh</button>
        </div>
      </div>
    </Fragment>
  );
};

export default Images;
